function applyTheme(e){document.body.classList.remove("theme-auto","theme-light","theme-dark"),document.body.classList.add(`theme-${e}`)}document.addEventListener("DOMContentLoaded",()=>{const e=localStorage.getItem("theme")||"auto";applyTheme(e);for(const t of document.querySelectorAll("#selTheme option"))t.selected=e===t.value;document.querySelector("#selTheme").addEventListener("change",function(){localStorage.setItem("theme",this.value),applyTheme(this.value)})});