
var LightboxZoom = function (target) {

    if (window.innerWidth < 1280) {
        console.log("Pinch to zoom para hacer zoom en dispositivos moviles.");
    }

    else {
        var magnifying_area = document.querySelector(".img-show"),
            magnifying_img = document.querySelector(".img-lightbox");
        magnifying_area.addEventListener("mousemove", function (event) {
            (clientX = event.clientX - magnifying_area.offsetLeft), (clientY = event.clientY - magnifying_area.offsetTop);
            var mWidth = magnifying_area.offsetWidth,
                mHeight = magnifying_area.offsetHeight;
            (clientX = (clientX / mWidth) * 50), (clientY = (clientY / mHeight) * 50), (magnifying_img.style.transform = "translate(-" + clientX + "%, -" + clientY + "%) scale(1.5)");
        }),
            magnifying_area.addEventListener("mouseleave", function () {
                magnifying_img.style.transform = "translate(0,0) scale(1)";
            });

            console.log("Se cargo correctamente el script zoom del lightbox.");
    }
};
