function CloseBtn(){const t=document.querySelector(".lightbox"),e=document.querySelector(".img-show"),o=document.querySelector(".back-btn");t.classList.toggle("lightbox-move"),e.classList.toggle("lightbox-show"),o.classList.toggle("lightbox-back")}const ZoomBtn=()=>{const t=document.querySelectorAll(".img-btn"),e=document.querySelector(".lightbox"),o=document.querySelector(".img-show"),c=document.querySelector(".copy"),l=document.querySelector(".img-lightbox"),s=document.querySelector(".back-btn");t.forEach(t=>{t.addEventListener("click",()=>{e.classList.toggle("lightbox-move"),o.classList.toggle("lightbox-show"),s.classList.toggle("lightbox-back"),g(t.getAttribute("src"),t.getAttribute("alt"))})});const g=(t,e)=>{l.src=t,c.innerHTML=e}};