
function NavBtn() {
        const nav = document.querySelector(".nav-links");
        nav.classList.toggle('nav-active');
};

function NavSection() {
        const menuBtn = document.querySelector("#menu-btn");
        const nav = document.querySelector(".nav-links");
        nav.classList.toggle("nav-active");
        menuBtn.classList.toggle('open');
};


function NavSocial() {
        const nav = document.querySelector(".nav-links");
        const menuBtn = document.querySelector("#menu-btn");
        nav.classList.toggle("nav-active");
         menuBtn.classList.toggle("open");
};